package common.library.utils;import java.io.File;import java.io.FileOutputStream;import java.io.InputStream;import android.content.Context;import android.content.res.AssetManager;public class FileUtils {	public static void copyAssetFileToSDCard( Context context, String assetFilePath, String sdCardFilePath ) {		if( new File(sdCardFilePath).exists() == true )			return;				AssetManager		assetMng = context.getAssets();		InputStream			is = null;		FileOutputStream	fos = null;				try {			is = assetMng.open(assetFilePath);											if( is == null )				return;			fos = new FileOutputStream(sdCardFilePath);						byte buf[] = new byte[2048];						int nLen = 0;			while( (nLen = is.read(buf)) > -1 ) {				fos.write(buf, 0, nLen);			}							}		catch( Exception e ) {			return;		}		finally {			try {				if( is != null )					is.close();				if( fos != null )					fos.close();			} catch ( Exception e ) {			}		}	}		public static final boolean checkExistFile( String path )	{		if( path == null || path.length() < 1 )			return false;				return new File(path).exists();	}	}